contracts/AntiRugHook.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {BaseHook} from "@uniswap/v4-periphery/src/utils/BaseHook.sol";
import {Hooks} from "@uniswap/v4-core/src/libraries/Hooks.sol";
import {IPoolManager} from "@uniswap/v4-core/src/interfaces/IPoolManager.sol";
import {PoolKey} from "@uniswap/v4-core/src/types/PoolKey.sol";
import {PoolId, PoolIdLibrary} from "@uniswap/v4-core/src/types/PoolId.sol";
import {
    BeforeSwapDelta,
    BeforeSwapDeltaLibrary
} from "@uniswap/v4-core/src/types/BeforeSwapDelta.sol";
import {Currency} from "@uniswap/v4-core/src/types/Currency.sol";
import {SwapParams} from "@uniswap/v4-core/src/types/PoolOperation.sol";

/**
 * @title AntiRugHook
 * @notice Uniswap v4 Hook that prevents founder token dumps during vesting period
 * @dev Implements beforeSwap hook to check if seller is founder and if vesting period has passed
 *
 * Prize Target: Uniswap v4 - "Agentic Finance" track
 *
 * Features:
 * - Blocks founder sells during lock period (default: 1 year)
 * - Allows gradual unlocking after cliff
 * - Emits events for transparency
 * - Configurable per-pool settings
 */
contract AntiRugHook is BaseHook {
    using PoolIdLibrary for PoolKey;

    // Vesting configuration per pool
    struct VestingConfig {
        address founder; // Founder address (restricted seller)
        uint256 lockStartTime; // When the lock period started
        uint256 cliffDuration; // Time before any tokens can be sold (e.g., 6 months)
        uint256 vestingDuration; // Total vesting period (e.g., 12 months)
        uint256 totalLocked; // Total tokens locked
        uint256 released; // Tokens already released
        bool initialized; // Whether config is set
    }

    // Pool ID => Vesting Config
    mapping(PoolId => VestingConfig) public vestingConfigs;

    // Events
    event VestingInitialized(
        PoolId indexed poolId,
        address indexed founder,
        uint256 cliffDuration,
        uint256 vestingDuration,
        uint256 totalLocked
    );

    event FounderSellBlocked(
        PoolId indexed poolId,
        address indexed founder,
        uint256 attemptedAmount,
        uint256 timeRemaining
    );

    event TokensReleased(
        PoolId indexed poolId,
        address indexed founder,
        uint256 amount
    );

    // Errors
    error VestingPeriodActive(uint256 timeRemaining);
    error NotEnoughVested(uint256 requested, uint256 available);
    error AlreadyInitialized();
    error NotInitialized();
    error InvalidDuration();

    constructor(IPoolManager _poolManager) BaseHook(_poolManager) {}

    /**
     * @notice Returns the hook permissions
     * @dev Only beforeSwap is needed for this hook
     */
    function getHookPermissions()
        public
        pure
        override
        returns (Hooks.Permissions memory)
    {
        return
            Hooks.Permissions({
                beforeInitialize: false,
                afterInitialize: true, // To set up vesting config
                beforeAddLiquidity: false,
                afterAddLiquidity: false,
                beforeRemoveLiquidity: false,
                afterRemoveLiquidity: false,
                beforeSwap: true, // Main hook - check founder sells
                afterSwap: false,
                beforeDonate: false,
                afterDonate: false,
                beforeSwapReturnDelta: false,
                afterSwapReturnDelta: false,
                afterAddLiquidityReturnDelta: false,
                afterRemoveLiquidityReturnDelta: false
            });
    }

    /**
     * @notice Initialize vesting for a pool after it's created
     * @param key Pool key
     * @param founder Address of the founder (restricted seller)
     * @param cliffDuration Time in seconds before any sells allowed
     * @param vestingDuration Total vesting period in seconds
     * @param totalLocked Total tokens subject to vesting
     */
    function initializeVesting(
        PoolKey calldata key,
        address founder,
        uint256 cliffDuration,
        uint256 vestingDuration,
        uint256 totalLocked
    ) external {
        PoolId poolId = key.toId();

        if (vestingConfigs[poolId].initialized) {
            revert AlreadyInitialized();
        }

        if (vestingDuration == 0 || cliffDuration > vestingDuration) {
            revert InvalidDuration();
        }

        vestingConfigs[poolId] = VestingConfig({
            founder: founder,
            lockStartTime: block.timestamp,
            cliffDuration: cliffDuration,
            vestingDuration: vestingDuration,
            totalLocked: totalLocked,
            released: 0,
            initialized: true
        });

        emit VestingInitialized(
            poolId,
            founder,
            cliffDuration,
            vestingDuration,
            totalLocked
        );
    }

    /**
     * @notice Hook called before every swap
     * @dev Blocks founder sells during vesting period
     */
    function _beforeSwap(
        address sender,
        PoolKey calldata key,
        SwapParams calldata params,
        bytes calldata
    ) internal override returns (bytes4, BeforeSwapDelta, uint24) {
        PoolId poolId = key.toId();
        VestingConfig storage config = vestingConfigs[poolId];

        // If no vesting config, allow all swaps
        if (!config.initialized) {
            return (
                BaseHook.beforeSwap.selector,
                BeforeSwapDeltaLibrary.ZERO_DELTA,
                0
            );
        }

        // Check if sender is the founder
        if (sender == config.founder) {
            // Check if this is a sell (founder selling their tokens)
            // In Uniswap v4, zeroForOne means selling token0 for token1
            bool isSelling = params.zeroForOne;

            if (isSelling) {
                uint256 timeElapsed = block.timestamp - config.lockStartTime;

                // Check if cliff period has passed
                if (timeElapsed < config.cliffDuration) {
                    uint256 timeRemaining = config.cliffDuration - timeElapsed;

                    emit FounderSellBlocked(
                        poolId,
                        config.founder,
                        params.amountSpecified > 0
                            ? uint256(params.amountSpecified)
                            : uint256(-params.amountSpecified),
                        timeRemaining
                    );

                    revert VestingPeriodActive(timeRemaining);
                }

                // Calculate vested amount after cliff
                uint256 vestedAmount = calculateVestedAmount(
                    config,
                    timeElapsed
                );
                uint256 availableToSell = vestedAmount - config.released;

                uint256 sellAmount = params.amountSpecified > 0
                    ? uint256(params.amountSpecified)
                    : uint256(-params.amountSpecified);

                if (sellAmount > availableToSell) {
                    revert NotEnoughVested(sellAmount, availableToSell);
                }

                // Update released amount
                config.released += sellAmount;

                emit TokensReleased(poolId, config.founder, sellAmount);
            }
        }

        return (
            BaseHook.beforeSwap.selector,
            BeforeSwapDeltaLibrary.ZERO_DELTA,
            0
        );
    }

    /**
     * @notice Calculate how many tokens have vested
     * @param config Vesting configuration
     * @param timeElapsed Time since lock started
     */
    function calculateVestedAmount(
        VestingConfig memory config,
        uint256 timeElapsed
    ) public pure returns (uint256) {
        if (timeElapsed < config.cliffDuration) {
            return 0;
        }

        if (timeElapsed >= config.vestingDuration) {
            return config.totalLocked;
        }

        // Linear vesting after cliff
        uint256 vestingTimeElapsed = timeElapsed - config.cliffDuration;
        uint256 vestingPeriod = config.vestingDuration - config.cliffDuration;

        return (config.totalLocked * vestingTimeElapsed) / vestingPeriod;
    }

    /**
     * @notice Get vesting status for a pool
     * @param key Pool key
     */
    function getVestingStatus(
        PoolKey calldata key
    )
        external
        view
        returns (
            bool initialized,
            address founder,
            uint256 totalLocked,
            uint256 vested,
            uint256 released,
            uint256 available,
            uint256 timeUntilFullyVested
        )
    {
        PoolId poolId = key.toId();
        VestingConfig memory config = vestingConfigs[poolId];

        if (!config.initialized) {
            return (false, address(0), 0, 0, 0, 0, 0);
        }

        uint256 timeElapsed = block.timestamp - config.lockStartTime;
        uint256 vestedAmount = calculateVestedAmount(config, timeElapsed);
        uint256 availableAmount = vestedAmount > config.released
            ? vestedAmount - config.released
            : 0;

        uint256 timeRemaining = 0;
        if (timeElapsed < config.vestingDuration) {
            timeRemaining = config.vestingDuration - timeElapsed;
        }

        return (
            true,
            config.founder,
            config.totalLocked,
            vestedAmount,
            config.released,
            availableAmount,
            timeRemaining
        );
    }

    /**
     * @notice After initialize hook - placeholder for future use
     */
    function _afterInitialize(
        address,
        PoolKey calldata,
        uint160,
        int24
    ) internal pure override returns (bytes4) {
        return BaseHook.afterInitialize.selector;
    }
}

contracts/Fundraiser.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title Fundraiser
 * @notice Handles public fundraising for the ConsulDAO encapsulated project
 */
contract Fundraiser is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // The treasury address where funds eventually go (HubDAO)
    address public treasury;

    // Supported Contribution Tokens (e.g., USDC)
    IERC20 public contributionToken;

    // Track contributions
    mapping(address => uint256) public contributions;
    uint256 public totalRaised;

    // Fundraising status
    bool public isLive;

    event ContributionReceived(address indexed contributor, uint256 amount);
    event FundsForwarded(address indexed treasury, uint256 amount);
    event FundraisingStateChanged(bool isOpen);

    constructor(
        address _initialOwner,
        address _treasury,
        address _contributionToken
    ) Ownable(_initialOwner) {
        treasury = _treasury;
        contributionToken = IERC20(_contributionToken);
    }

    /**
     * @notice Toggle fundraising state
     */
    function setFundraisingState(bool _isOpen) external onlyOwner {
        isLive = _isOpen;
        emit FundraisingStateChanged(_isOpen);
    }

    /**
     * @notice Contribute funds (ERC20)
     * @param amount Amount to contribute
     */
    function contribute(uint256 amount) external nonReentrant {
        require(isLive, "Fundraising not active");
        require(amount > 0, "Amount must be > 0");

        // Transfer tokens from user to this contract
        contributionToken.safeTransferFrom(msg.sender, address(this), amount);

        contributions[msg.sender] += amount;
        totalRaised += amount;

        emit ContributionReceived(msg.sender, amount);
    }

    /**
     * @notice Forward all held funds to the treasury
     */
    function forwardToTreasury() external onlyOwner {
        uint256 balance = contributionToken.balanceOf(address(this));
        require(balance > 0, "No funds to forward");

        contributionToken.safeTransfer(treasury, balance);

        emit FundsForwarded(treasury, balance);
    }

    /**
     * @notice Update treasury address
     */
    function setTreasury(address newTreasury) external onlyOwner {
        require(newTreasury != address(0), "Invalid address");
        treasury = newTreasury;
    }
}

contracts/HubDAO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title HubDAO
 * @notice Main DAO contract that holds the treasury, approves quarterly budgets, and has veto power
 */
interface ISquads {
    function fundSquadBudget(uint256 squadId, uint256 amount) external;
}

contract HubDAO is Ownable, ReentrancyGuard {
    // Treasury token (e.g., USDC)
    IERC20 public treasuryToken;
    address public squadsContract;

    // Quarterly budget structure
    struct QuarterlyBudget {
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        bool approved;
        mapping(address => bool) votes;
        uint256 voteCount;
    }

    // Current quarter budget
    mapping(uint256 => QuarterlyBudget) public quarterlyBudgets;
    uint256 public currentQuarter;

    // Minimum voting threshold (in basis points, e.g., 5000 = 50%)
    uint256 public constant MIN_VOTING_THRESHOLD = 5000;

    // Veto power addresses
    mapping(address => bool) public vetoPowerHolders;

    // Events
    event BudgetProposed(uint256 indexed quarter, uint256 amount);
    event BudgetVoted(
        uint256 indexed quarter,
        address indexed voter,
        bool support
    );
    event BudgetApproved(uint256 indexed quarter, uint256 amount);
    event BudgetExecuted(
        uint256 indexed quarter,
        address indexed recipient,
        uint256 amount
    );
    event VetoExecuted(uint256 indexed quarter, address indexed vetoer);

    constructor(
        address _treasuryToken,
        address _initialOwner
    ) Ownable(_initialOwner) {
        treasuryToken = IERC20(_treasuryToken);
        currentQuarter = 0;
    }

    /**
     * @notice Propose a quarterly budget
     * @param amount Budget amount in treasury token
     */
    function proposeBudget(uint256 amount) external onlyOwner {
        currentQuarter++;
        QuarterlyBudget storage budget = quarterlyBudgets[currentQuarter];
        budget.amount = amount;
        budget.startTime = block.timestamp;
        budget.endTime = block.timestamp + 90 days; // 3 months
        budget.approved = false;

        emit BudgetProposed(currentQuarter, amount);
    }

    /**
     * @notice Vote on a quarterly budget
     * @param quarter Quarter number
     * @param support Whether to support the budget
     */
    function voteOnBudget(uint256 quarter, bool support) external {
        QuarterlyBudget storage budget = quarterlyBudgets[quarter];
        require(block.timestamp <= budget.endTime, "Voting period ended");
        require(!budget.votes[msg.sender], "Already voted");

        budget.votes[msg.sender] = true;
        if (support) {
            budget.voteCount++;
        }

        emit BudgetVoted(quarter, msg.sender, support);
    }

    /**
     * @notice Approve budget if voting threshold is met
     * @param quarter Quarter number
     */
    function approveBudget(uint256 quarter) external {
        QuarterlyBudget storage budget = quarterlyBudgets[quarter];
        require(!budget.approved, "Already approved");
        require(block.timestamp <= budget.endTime, "Voting period ended");

        // Check if voting threshold is met (simplified - should check token balance)
        require(
            budget.voteCount >= MIN_VOTING_THRESHOLD / 100,
            "Insufficient votes"
        );

        budget.approved = true;

        emit BudgetApproved(quarter, budget.amount);
    }

    /**
     * @notice Execute budget payment to recipient
     * @param quarter Quarter number
     * @param recipient Address to receive funds
     * @param amount Amount to transfer
     */
    function executeBudget(
        uint256 quarter,
        address recipient,
        uint256 amount
    ) external onlyOwner nonReentrant {
        QuarterlyBudget storage budget = quarterlyBudgets[quarter];
        require(budget.approved, "Budget not approved");
        require(
            block.timestamp >= budget.startTime,
            "Budget period not started"
        );
        require(block.timestamp <= budget.endTime, "Budget period ended");
        require(amount <= budget.amount, "Amount exceeds budget");

        require(treasuryToken.transfer(recipient, amount), "Transfer failed");

        emit BudgetExecuted(quarter, recipient, amount);
    }

    /**
     * @notice Veto a budget (only veto power holders)
     * @param quarter Quarter number
     */
    function vetoBudget(uint256 quarter) external {
        require(vetoPowerHolders[msg.sender], "Not authorized to veto");
        QuarterlyBudget storage budget = quarterlyBudgets[quarter];
        require(budget.approved, "Budget not approved");

        budget.approved = false;

        emit VetoExecuted(quarter, msg.sender);
    }

    /**
     * @notice Add or remove veto power holder
     * @param holder Address to grant/revoke veto power
     * @param hasPower Whether to grant veto power
     */
    function setVetoPower(address holder, bool hasPower) external onlyOwner {
        vetoPowerHolders[holder] = hasPower;
    }

    function setSquadsContract(address _squadsContract) external onlyOwner {
        squadsContract = _squadsContract;
    }

    /**
     * @notice Allocate funds to a specific squad
     * @dev Transfers tokens to Squads contract and updates budget mapping there
     */
    function allocateSquadBudget(
        uint256 squadId,
        uint256 amount
    ) external onlyOwner nonReentrant {
        require(squadsContract != address(0), "Squads contract not set");

        // Transfer tokens to Squads contract
        require(
            treasuryToken.transfer(squadsContract, amount),
            "Transfer failed"
        );

        // Update budget in Squads contract
        ISquads(squadsContract).fundSquadBudget(squadId, amount);
    }

    /**
     * @notice Get treasury balance
     * @return Balance of treasury token
     */
    function getTreasuryBalance() external view returns (uint256) {
        return treasuryToken.balanceOf(address(this));
    }
}

contracts/ProjectRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title ProjectRegistry
 * @notice Simple on-chain registry for ConsulDAO incubated projects
 * @dev Stores project manifests similar to ENS text records, but optimized for L2
 * 
 * Prize Target: ENS - "Creative DeFi Use of ENS"
 * We demonstrate ENS-like functionality with on-chain identity storage
 */
contract ProjectRegistry {

    struct Project {
        string name;
        string manifest;      // JSON manifest (like ENS text record)
        address founder;
        uint256 registeredAt;
        bool exists;
    }

    // Project name => Project data
    mapping(string => Project) public projects;

    // Founder address => list of project names
    mapping(address => string[]) public founderProjects;

    // Total projects registered
    uint256 public totalProjects;

    // Events
    event ProjectRegistered(
        string indexed nameHash,
        string name,
        address indexed founder,
        string manifest,
        uint256 timestamp
    );

    event ProjectUpdated(
        string indexed nameHash,
        string name,
        string manifest,
        uint256 timestamp
    );

    // Errors
    error ProjectAlreadyExists(string name);
    error ProjectNotFound(string name);
    error NotProjectOwner(string name, address caller);
    error InvalidName();

    /**
     * @notice Register a new project
     * @param name Project name (will be the subdomain-like identifier)
     * @param manifest JSON string containing project metadata
     */
    function registerProject(string calldata name, string calldata manifest) external {
        if (bytes(name).length == 0 || bytes(name).length > 32) {
            revert InvalidName();
        }

        if (projects[name].exists) {
            revert ProjectAlreadyExists(name);
        }

        projects[name] = Project({
            name: name,
            manifest: manifest,
            founder: msg.sender,
            registeredAt: block.timestamp,
            exists: true
        });

        founderProjects[msg.sender].push(name);
        totalProjects++;

        emit ProjectRegistered(name, name, msg.sender, manifest, block.timestamp);
    }

    /**
     * @notice Update project manifest (only founder can update)
     * @param name Project name
     * @param manifest New manifest JSON
     */
    function updateManifest(string calldata name, string calldata manifest) external {
        if (!projects[name].exists) {
            revert ProjectNotFound(name);
        }

        if (projects[name].founder != msg.sender) {
            revert NotProjectOwner(name, msg.sender);
        }

        projects[name].manifest = manifest;

        emit ProjectUpdated(name, name, manifest, block.timestamp);
    }

    /**
     * @notice Get project details
     * @param name Project name
     */
    function getProject(string calldata name) external view returns (
        string memory projectName,
        string memory manifest,
        address founder,
        uint256 registeredAt,
        bool exists
    ) {
        Project memory p = projects[name];
        return (p.name, p.manifest, p.founder, p.registeredAt, p.exists);
    }

    /**
     * @notice Get all projects by founder
     * @param founder Founder address
     */
    function getProjectsByFounder(address founder) external view returns (string[] memory) {
        return founderProjects[founder];
    }

    /**
     * @notice Check if project name is available
     * @param name Project name to check
     */
    function isNameAvailable(string calldata name) external view returns (bool) {
        return !projects[name].exists;
    }
}

contracts/Squads.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title Squads
 * @notice Manages specialized squads (e.g., Legal, Dev) and their tasks
 */
contract Squads is Ownable, ReentrancyGuard {
    struct Task {
        string description;
        uint256 reward;
        address assignee;
        bool completed;
        bool approved;
        address creator;
    }

    struct Squad {
        string name;
        address[] members;
        mapping(address => bool) isMember;
        uint256 budget;
        // Tasks
        mapping(uint256 => Task) tasks;
        uint256 taskCount;
    }

    // Squad ID => Squad
    mapping(uint256 => Squad) public squads;
    uint256 public squadCount;

    // HubDAO address (for budget requests - future integration)
    address public hubDao;

    event SquadCreated(uint256 indexed squadId, string name);
    event MemberAdded(uint256 indexed squadId, address member);
    event MemberRemoved(uint256 indexed squadId, address member);
    event TaskCreated(
        uint256 indexed squadId,
        uint256 indexed taskId,
        string description,
        uint256 reward
    );
    event TaskAssigned(
        uint256 indexed squadId,
        uint256 indexed taskId,
        address assignee
    );
    event TaskCompleted(uint256 indexed squadId, uint256 indexed taskId);
    event BudgetAllocated(uint256 indexed squadId, uint256 amount);

    constructor(address _initialOwner) Ownable(_initialOwner) {}

    modifier onlyHubOrOwner() {
        require(
            msg.sender == owner() || msg.sender == hubDao,
            "Not authorized"
        );
        _;
    }

    function setHubDao(address _hubDao) external onlyOwner {
        hubDao = _hubDao;
    }

    /**
     * @notice Add funds to a squad's budget
     */
    function fundSquadBudget(
        uint256 squadId,
        uint256 amount
    ) external onlyHubOrOwner {
        squads[squadId].budget += amount;
        emit BudgetAllocated(squadId, amount);
    }

    modifier onlySquadMember(uint256 squadId) {
        require(squads[squadId].isMember[msg.sender], "Not a squad member");
        _;
    }

    /**
     * @notice Create a new squad
     * @param name Name of the squad (e.g. "Legal")
     */
    function createSquad(string calldata name) external onlyOwner {
        squadCount++;
        Squad storage newSquad = squads[squadCount];
        newSquad.name = name;
        // Owner is default member? No, let's keep it specific.

        emit SquadCreated(squadCount, name);
    }

    /**
     * @notice Add a member to a squad
     * @param squadId ID of the squad
     * @param member Address to add
     */
    function addMember(uint256 squadId, address member) external onlyOwner {
        require(squadId > 0 && squadId <= squadCount, "Invalid squad ID");
        require(!squads[squadId].isMember[member], "Already a member");

        squads[squadId].isMember[member] = true;
        squads[squadId].members.push(member);

        emit MemberAdded(squadId, member);
    }

    /**
     * @notice Create a task within a squad
     * @param squadId ID of the squad
     * @param description Task description
     * @param reward Reward amount (in treasury token units)
     */
    function createTask(
        uint256 squadId,
        string calldata description,
        uint256 reward
    ) external onlySquadMember(squadId) {
        Squad storage squad = squads[squadId];

        squad.taskCount++;
        Task storage newTask = squad.tasks[squad.taskCount];
        newTask.description = description;
        newTask.reward = reward;
        newTask.creator = msg.sender;

        emit TaskCreated(squadId, squad.taskCount, description, reward);
    }

    /**
     * @notice Assign a task to a member
     * @param squadId ID of the squad
     * @param taskId ID of the task
     * @param assignee Address to assign
     */
    function assignTask(
        uint256 squadId,
        uint256 taskId,
        address assignee
    ) external onlySquadMember(squadId) {
        Squad storage squad = squads[squadId];
        require(squad.isMember[assignee], "Assignee not in squad");
        require(taskId > 0 && taskId <= squad.taskCount, "Invalid task ID");

        squad.tasks[taskId].assignee = assignee;

        emit TaskAssigned(squadId, taskId, assignee);
    }

    /**
     * @notice Mark task as completed (by assignee)
     * @param squadId ID of the squad
     * @param taskId ID of the task
     */
    function completeTask(uint256 squadId, uint256 taskId) external {
        // Any member or just assignee? Let's say assignee.
        Squad storage squad = squads[squadId];
        Task storage task = squad.tasks[taskId];

        require(task.assignee == msg.sender, "Not assignee");
        require(!task.completed, "Already completed");

        task.completed = true;

        emit TaskCompleted(squadId, taskId);
    }

    // Future: Approval mechanism where another member verifies works and triggers payment via HubDAO

    /**
     * @notice Get squad details (helper)
     */
    function getSquadName(
        uint256 squadId
    ) external view returns (string memory) {
        return squads[squadId].name;
    }
}

